mapper.update:
update tbl_board set title =#{title}, content
=#{content}, writer=#{writer}
where bno = #{bno}

mapper.select:
 
select * from (
   bno, title, content, writer, regdate, viewcnt 
 from 
   tbl_board  where bno > 0 
   ) where rownum <= #{page} 
 order by bno desc, regdate desc

select * from( select rownum rnum, bno, title, content, writer, regdate, viewcnt from ( select bno, title, content, writer, regdate, viewcnt from tbl_board where bno > 0
order by bno desc, regdate desc
)) where (rnum >= #{pageStart} AND rnum <= #{pageEnd}) order by bno desc, regdate desc
